    bytes_read = read(client_fd, buffer, sizeof(buffer) - 1);
    if (bytes_read == -1) {
        perror("read");
        close(client_fd);
        return;
    } else if (bytes_read == 0) {
        // Client closed the connection
        printf("Client disconnected\n");
        close(client_fd);
        return;
    }

    buffer[bytes_read] = '\0';
    printf("Received: %s\n", buffer);

    // Echo the message back to the client
